import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import products from "../products";
import Rating from "../components/Rating";

const ProductScreen = () => {
  const { id: productId } = useParams();
  const product = products.find((p) => p._id === productId);

  return (
    <div className="mx-24 md:mx-6 overflow-hidden">
      <div className="flex justify-center items-center mt-24 text-lg w-20 h-10 rounded-sm hover:bg-gray-400 text-black  bg-slate-200 ">
        <Link to="/">
          <p className="m-1">Go Back</p>
        </Link>
      </div>
      <div className="flex md:flex-col justify-between">
        <div className="my-4 rounded-md">
          <img
            src={product.image}
            alt=""
            className="h-80 w-80 md:h-72 md:w-72"
          />
        </div>
        <div className="text-2xl">
          <span>{product.name}</span>
          <div>
            <Rating rating={product.rating} noReview={product.numReviews} />
          </div>
          <div>{product.price}</div>
        </div>
        <div>
          <div>
            <p>Price</p>
            <p>{product.price}</p>
          </div>
          <div>
            <p>status:</p>
            <p>{product.countInStock ? "In Stock" : "Stock"}</p>
          </div>
          <div className="flex justify-center items-center font-mont text-lg w-28 h-12 rounded-md hover:bg-gray-800 text-black  bg-slate-600 ">
            <p className="m-1">Add To Cart</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductScreen;
