import React from "react";

const Product = ({ product }) => {
  return (
    <div className="flex justify-center my-10">
      <div className="md:h-80 md:w-72 h-80 w-72 rounded-lg border-2 border-gray-500 shadow-xl">
        <img className="p-4 " src={product.image} alt="" />
        <div className="pl-4">
          <h3 className="text-lg font-mono">{product.name}</h3>
          <h1 className="text-xl font-bold font-mono">{product.price}</h1>
        </div>
      </div>
    </div>
  );
};

export default Product;
