import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import products from "../products";
import Rating from "../components/Rating";

const ProductScreen = () => {
  const { id: productId } = useParams();
  const product = products.find((p) => p._id === productId);

  return (
    <div>
      <div className="flex justify-center items-center mt-24 text-lg w-20 h-10 rounded-sm hover:bg-gray-400 text-black mx-24 md:mx-6 bg-slate-200 ">
        <Link to="/">
          <p className="m-1">Go Back</p>
        </Link>
      </div>
      <div>
        <img src={product.image} alt="" className="h-80 w-80" />
      </div>
    </div>
  );
};

export default ProductScreen;
