import React from "react";

const Product = ({ product }) => {
  return (
    <div className="mx-3 p-3 rounded-lg border-2 border-gray-500">
      <img
        className="p-6 rounded-lg border-2 border-gray-500"
        src={product.image}
        alt=""
      />
      <div className="flex justify-center text-lg   ">
        <strong className="m-1">{product.name}</strong>
        <strong className="m-1">{product.price}</strong>
      </div>
    </div>
  );
};

export default Product;
